INSERT INTO `wp_es_sentdetails` VALUES("1", "oaceyx-vxighs-drhfve-tfivnj-gjmwlo", "0", "Newsletter", "2017-07-25 05:46:53", "2017-07-25 05:46:55", "2", "<strong style=\"color: #990000\">What can you achieve using Email Subscribers?</strong><p>Add subscription forms on website, send HTML newsletters & automatically notify subscribers about new blog posts once it is published. You can also Import or Export subscribers from any list to Email Subscribers.</p> <strong style=\"color: #990000\">Plugin Features</strong><ol> <li>Send notification emails to subscribers when new blog posts are published.</li> <li>Subscribe form available with 3 options to setup.</li> <li>Double Opt-In and Single Opt-In support.</li> <li>Email notification to admin when a new user signs up (Optional).</li> <li>Automatic welcome email to subscriber.</li> <li>Auto add unsubscribe link in the email.</li> <li>Import/Export subscriber emails to migrate to any lists.</li> <li>Default WordPress editor to compose emails.</li> </ol> <strong>Thanks & Regards,</strong><br>Admin", "Sent", "Immediately", "Welcome To Email Subscribers");
INSERT INTO `wp_es_templatetable` VALUES("1", "New Post Published - ###POSTTITLE###", "Hello ###NAME###,\r\n\r\nWe have published a new blog article on our website : ###POSTTITLE###\r\n###POSTIMAGE###\r\n\r\nYou can view it from this link : ###POSTLINK###\r\n\r\nThanks & Regards,\r\nAdmin\r\n\r\nYou received this email because in the past you have provided us your email address : ###EMAIL### to receive notifications when new updates are posted.", "Published", "Post Notification");
INSERT INTO `wp_es_templatetable` VALUES("2", "Welcome To Email Subscribers", "<strong style=\"color: #990000\">What can you achieve using Email Subscribers?</strong><p>Add subscription forms on website, send HTML newsletters & automatically notify subscribers about new blog posts once it is published. You can also Import or Export subscribers from any list to Email Subscribers.</p> <strong style=\"color: #990000\">Plugin Features</strong><ol> <li>Send notification emails to subscribers when new blog posts are published.</li> <li>Subscribe form available with 3 options to setup.</li> <li>Double Opt-In and Single Opt-In support.</li> <li>Email notification to admin when a new user signs up (Optional).</li> <li>Automatic welcome email to subscriber.</li> <li>Auto add unsubscribe link in the email.</li> <li>Import/Export subscriber emails to migrate to any lists.</li> <li>Default WordPress editor to compose emails.</li> </ol> <strong>Thanks & Regards,</strong><br>Admin", "Published", "Newsletter");
